(async function(ctx, { x, y }) {
    // await ctx.act('hbriidge driver label', { in1: true, in2: false });
    // await ctx.act('pwm hbridge driver label', { in1: true, in2: false,  });
    // await ctx.act('pwm driver label', { in1: true, in2: false });

    const duty = ctx.state['Speed0']?.value ?? 0.0;
    const ax = Math.abs(x);
    const ay = Math.abs(y);

    if (ax < 0.2 && y > 0.9) {
         await ctx.act([
            ['MoveFrontR', { in1: true, in2: false, duty }],
            ['MoveFrontL', { in1: true, in2: false, duty }],
            ['MoveRearR', { in1: true, in2: false, duty }],
            ['MoveRearL', { in1: true, in2: false, duty }]
         ]);
    } 
    else if (ax < 0.2 && y < -0.9) {
        await ctx.act([
            ['MoveFrontR', { in1: false, in2: true, duty }],
            ['MoveFrontL', { in1: false, in2: true, duty }],
            ['MoveRearR', { in1: false, in2: true, duty }],
            ['MoveRearL', { in1: false, in2: true, duty }]
         ]);
    }
    else if (x > 0.9 && ay < 0.2) {
        await ctx.act([
            ['MoveFrontR', { in1: false, in2: true, duty }],
            ['MoveFrontL', { in1: true, in2: false, duty }],
            ['MoveRearR', { in1: true, in2: false, duty }],
            ['MoveRearL', { in1: false, in2: true, duty }]
         ]);
    }
    else if (x < -0.9 && ay < 0.2) {
        await ctx.act([
            ['MoveFrontR', { in1: true, in2: false, duty }],
            ['MoveFrontL', { in1: false, in2: true, duty }],
            ['MoveRearR', { in1: false, in2: true, duty }],
            ['MoveRearL', { in1: true, in2: false, duty }]
         ]);
    }
    else if (x > 0.5 && y > 0.5) {
        await ctx.act([
            ['MoveFrontR', { in1: false, in2: false, duty }],
            ['MoveFrontL', { in1: true, in2: false, duty }],
            ['MoveRearR', { in1: true, in2: false, duty }],
            ['MoveRearL', { in1: false, in2: false, duty }]
         ]);
    }
    else if (x < -0.5 && y > 0.5) {
        await ctx.act([
            ['MoveFrontR', { in1: true, in2: false, duty }],
            ['MoveFrontL', { in1: false, in2: false, duty }],
            ['MoveRearR', { in1: false, in2: false, duty }],
            ['MoveRearL', { in1: true, in2: false, duty }]
         ]);
    }
    else if (x < -0.5 && y < -0.5) {
        await ctx.act([
            ['MoveFrontR', { in1: false, in2: true, duty }],
            ['MoveFrontL', { in1: false, in2: false, duty }],
            ['MoveRearR', { in1: false, in2: false, duty }],
            ['MoveRearL', { in1: false, in2: true, duty }]
         ]);
    }
    else if (x > 0.5 && y < -0.5) {
        await ctx.act([
            ['MoveFrontR', { in1: false, in2: false, duty }],
            ['MoveFrontL', { in1: false, in2: true, duty }],
            ['MoveRearR', { in1: false, in2: true, duty }],
            ['MoveRearL', { in1: false, in2: false, duty }]
         ]);
    }
})

